#!/usr/bin/env python3
"""
BTDocs Style Validator - Validates BuilderHub documentation against style guidelines

This tool examines documentation changes and ensures they follow the BTDocs style guide.
Based on analysis of code reviews and established patterns in the BTDocs-BONES package.

Usage:
    python btdocs-style-validator.py <file_path> [--verbose] [--suggest-fixes]

Author: BuilderHub Documentation Team
"""

import re
import sys
import argparse
import json
from pathlib import Path
from typing import List, Dict, Tuple, Optional
from dataclasses import dataclass


@dataclass
class ValidationIssue:
    """Represents a style validation issue"""
    line_number: int
    issue_type: str
    message: str
    severity: str  # 'error', 'warning', 'suggestion'
    fix_suggestion: Optional[str] = None


class BTDocsStyleValidator:
    """Validates BTDocs documentation against style guidelines"""

    def __init__(self, verbose: bool = False):
        self.verbose = verbose
        self.issues: List[ValidationIssue] = []

        # Common reference patterns from cached analysis
        self.reference_patterns = {
            'event_consumers': r'^event-consumer-[a-z-]+-[a-z-]+$',
            'web_services': r'^golden-path-[a-z-]+-[a-z-]+$',
            'developer_guide': r'^(hello-[a-z-]+-[a-z-]+|[a-z-]+-[a-z-]+)$',
        }

        # AWS documentation link patterns
        self.aws_link_patterns = {
            'aws_docs': r':awsdocs:`([^<]+)<([^>]+)>`',
            'aws_service': r':aws:`([^<]+)<([^>]+)>`',
            'wikipedia': r':wikipedia:`([^<]+)<([^>]+)>`',
        }

    def validate_file(self, file_path: Path) -> List[ValidationIssue]:
        """Validate a single RST file"""
        self.issues = []

        if not file_path.exists():
            return self.issues

        with open(file_path, 'r', encoding='utf-8') as f:
            lines = f.readlines()

        # Determine doc type from path
        doc_type = self._determine_doc_type(file_path)

        # Run all validation checks
        self._check_header_structure(lines)
        self._check_line_length(lines)
        self._check_heading_case(lines)
        self._check_writing_style(lines)
        self._check_reference_labels(lines, doc_type)
        self._check_link_formatting(lines)
        self._check_directive_usage(lines)
        self._check_table_formatting(lines)
        self._check_cross_references(lines)

        return self.issues

    def _determine_doc_type(self, file_path: Path) -> str:
        """Determine documentation type from file path"""
        path_str = str(file_path)
        if 'event_consumers' in path_str:
            return 'event_consumers'
        elif 'web_services' in path_str:
            return 'web_services'
        elif 'developer_guide' in path_str:
            return 'developer_guide'
        elif 'golden_path' in path_str:
            return 'golden_path'
        return 'unknown'

    def _check_header_structure(self, lines: List[str]) -> None:
        """Check for proper RST header structure"""
        # Check copyright header
        if not any('Copyright' in line for line in lines[:10]):
            self.issues.append(ValidationIssue(
                line_number=1,
                issue_type='missing_copyright',
                message='Missing copyright header',
                severity='error',
                fix_suggestion='Add copyright header: .. Copyright © 2025, Amazon, LLC.'
            ))

        # Check for contribution guide reference
        if not any('https://docs.hub.amazon.dev/contributing/' in line for line in lines[:15]):
            self.issues.append(ValidationIssue(
                line_number=1,
                issue_type='missing_contribution_link',
                message='Missing contribution guide link',
                severity='warning',
                fix_suggestion='Add contribution guide link after copyright'
            ))

    def _check_line_length(self, lines: List[str]) -> None:
        """Check line length (should be ~100 chars)"""
        for i, line in enumerate(lines, 1):
            # Skip directives and special lines
            if line.strip().startswith(('..', ':', '|', '   ')) or not line.strip():
                continue

            if len(line.rstrip()) > 100:
                self.issues.append(ValidationIssue(
                    line_number=i,
                    issue_type='line_too_long',
                    message=f'Line length {len(line.rstrip())} exceeds 100 characters',
                    severity='warning',
                    fix_suggestion='Break line at natural word boundaries around 100 characters'
                ))

    def _check_heading_case(self, lines: List[str]) -> None:
        """Check heading capitalization (sentence case)"""
        heading_chars = ['=', '-', '^', '~', '#']

        for i in range(len(lines) - 1):
            line = lines[i].strip()
            next_line = lines[i + 1].strip() if i + 1 < len(lines) else ''

            # Check if next line is a heading underline
            if next_line and len(set(next_line)) == 1 and next_line[0] in heading_chars:
                if line and not self._is_sentence_case(line):
                    self.issues.append(ValidationIssue(
                        line_number=i + 1,
                        issue_type='heading_case',
                        message=f'Heading should use sentence case: "{line}"',
                        severity='error',
                        fix_suggestion=f'Change to: "{self._to_sentence_case(line)}"'
                    ))

    def _check_writing_style(self, lines: List[str]) -> None:
        """Check for second person writing style"""
        third_person_patterns = [
            r'\b(one|builders?|developers?|users?|they|their|them)\s+(should|must|can|may|might)\b',
            r'\b(the\s+builder|the\s+developer|the\s+user)\b',
        ]

        second_person_replacements = {
            'builders should': 'you should',
            'builders must': 'you must',
            'developers should': 'you should',
            'one should': 'you should',
            'one must': 'you must',
            'the builder': 'you',
            'the developer': 'you',
        }

        for i, line in enumerate(lines, 1):
            for pattern in third_person_patterns:
                if re.search(pattern, line, re.IGNORECASE):
                    # Find the specific match
                    match = re.search(pattern, line, re.IGNORECASE)
                    if match:
                        matched_text = match.group(0).lower()
                        suggestion = second_person_replacements.get(matched_text, 'you')

                        self.issues.append(ValidationIssue(
                            line_number=i,
                            issue_type='writing_style',
                            message=f'Use second person ("you") instead of "{match.group(0)}"',
                            severity='error',
                            fix_suggestion=f'Replace with "{suggestion}"'
                        ))

    def _check_reference_labels(self, lines: List[str], doc_type: str) -> None:
        """Check reference label formatting"""
        reference_pattern = r'^\.\.\s+_([a-zA-Z0-9-]+):\s*$'

        for i, line in enumerate(lines, 1):
            match = re.match(reference_pattern, line)
            if match:
                label = match.group(1)

                # Check if label follows expected pattern for doc type
                if doc_type in self.reference_patterns:
                    expected_pattern = self.reference_patterns[doc_type]
                    if not re.match(expected_pattern, label):
                        self.issues.append(ValidationIssue(
                            line_number=i,
                            issue_type='reference_label_format',
                            message=f'Reference label "{label}" doesn\'t follow {doc_type} pattern',
                            severity='warning',
                            fix_suggestion=f'Use pattern like: event-consumer-topic-action'
                        ))

    def _check_link_formatting(self, lines: List[str]) -> None:
        """Check for proper link formatting"""
        # Check for raw URLs that should use roles
        url_patterns = {
            r'https?://docs\.aws\.amazon\.com/': ':awsdocs:',
            r'https?://aws\.amazon\.com/': ':aws:',
            r'https?://en\.wikipedia\.org/': ':wikipedia:',
        }

        for i, line in enumerate(lines, 1):
            for pattern, role in url_patterns.items():
                if re.search(pattern, line):
                    # Check if it's already using the role
                    if role not in line:
                        self.issues.append(ValidationIssue(
                            line_number=i,
                            issue_type='link_formatting',
                            message=f'Use {role} role instead of raw URL',
                            severity='error',
                            fix_suggestion=f'Replace with {role}`link text <path>`'
                        ))

    def _check_directive_usage(self, lines: List[str]) -> None:
        """Check for proper RST directive usage"""
        # Check for note and warning directives
        for i, line in enumerate(lines, 1):
            if line.strip().startswith('.. note:'):
                # Should be .. note:: (with double colon)
                if not line.strip().startswith('.. note::'):
                    self.issues.append(ValidationIssue(
                        line_number=i,
                        issue_type='directive_syntax',
                        message='Note directive missing double colon',
                        severity='error',
                        fix_suggestion='Use ".. note::" with double colon'
                    ))

            if line.strip().startswith('.. warning:'):
                if not line.strip().startswith('.. warning::'):
                    self.issues.append(ValidationIssue(
                        line_number=i,
                        issue_type='directive_syntax',
                        message='Warning directive missing double colon',
                        severity='error',
                        fix_suggestion='Use ".. warning::" with double colon'
                    ))

    def _check_table_formatting(self, lines: List[str]) -> None:
        """Check for tables vs bullet lists for complex data"""
        # Simple heuristic: if we see many bullet points with colons, suggest a table
        bullet_count = 0
        bullet_start = -1

        for i, line in enumerate(lines, 1):
            if line.strip().startswith('* ') and ':' in line:
                if bullet_start == -1:
                    bullet_start = i
                bullet_count += 1
            else:
                if bullet_count >= 5:  # Threshold for suggesting table
                    self.issues.append(ValidationIssue(
                        line_number=bullet_start,
                        issue_type='table_suggestion',
                        message=f'Consider using a table instead of {bullet_count} bullet points',
                        severity='suggestion',
                        fix_suggestion='Use .. list-table:: directive for structured data'
                    ))
                bullet_count = 0
                bullet_start = -1

    def _check_cross_references(self, lines: List[str]) -> None:
        """Check for missing cross-references"""
        # Look for terms that likely should be cross-referenced
        terms_to_reference = {
            'Lambda': 'golden-path-lambda',
            'ECS Fargate': 'golden-path-ecs-fargate',
            'SQS': 'event-consumer-prefer-sqs-for-messaging',
            'Kinesis': 'event-consumer-stream-processing-prefer-kinesis',
            'Step Functions': 'event-consumer-orchestration-prefer-step-functions',
        }

        for i, line in enumerate(lines, 1):
            for term, expected_ref in terms_to_reference.items():
                if term in line and f':ref:`' not in line and f'.. _{expected_ref}' not in line:
                    # Check if it's in a code block or directive
                    if not line.strip().startswith(('..', ':', '    ', '``')):
