        for issue in errors:
            report.append(f"  Line {issue.line_number}: {issue.message}")
            if issue.fix_suggestion:
                report.append(f"    💡 Fix: {issue.fix_suggestion}")

    if warnings:
        report.append(f"\n⚠️  Warnings ({len(warnings)}):")
        for issue in warnings:
            report.append(f"  Line {issue.line_number}: {issue.message}")
            if issue.fix_suggestion:
                report.append(f"    💡 Fix: {issue.fix_suggestion}")

    if suggestions:
        report.append(f"\n💭 Suggestions ({len(suggestions)}):")
        for issue in suggestions:
            report.append(f"  Line {issue.line_number}: {issue.message}")
            if issue.fix_suggestion:
                report.append(f"    💡 Fix: {issue.fix_suggestion}")

    report.append(f"\n📊 Summary: {len(errors)} errors, {len(warnings)} warnings, {len(suggestions)} suggestions")

    return '\n'.join(report)


def main():
    """Main entry point"""
    parser = argparse.ArgumentParser(
        description='Validate BTDocs documentation against style guidelines'
    )
    parser.add_argument('file_path', help='Path to RST file to validate')
    parser.add_argument('--verbose', '-v', action='store_true', 
                        help='Enable verbose output')
    parser.add_argument('--json', action='store_true',
                        help='Output results as JSON')
    parser.add_argument('--suggest-fixes', action='store_true',
