            line = lines[i].strip()
            next_line = lines[i + 1].strip() if i + 1 < len(lines) else ''

            # Check if next line is a heading underline
            if next_line and len(set(next_line)) == 1 and next_line[0] in heading_chars:
                if line and not self._is_sentence_case(line):
