        return new DeploymentPipeline(this, `${tenant.name}-pipeline`, {
          createLegacyPipelineStage: false,
          pipelineName: `tenant-${tenant.name}`,

          // Tier-based configuration
          approvalWorkflows: tenant.tier === 'enterprise' 
            ? ['SecurityReview', 'ChangeManagement']
            : ['StandardReview'],

          // Regional deployment
          regions: tenant.regions,

          // Custom domain setup
          customStages: tenant.customDomains ? [{
            name: 'DomainConfiguration',
            actions: tenant.customDomains.map(d => `ConfigureDomain:${d}`)
          }] : []
