**Scenario 2: Pipeline structure file corruption**

.. code-block:: python

   #!/usr/bin/env python3
   # Validate and repair pipeline structure files

   import json
   import jsonschema
   import sys

   # Pipeline structure schema
   SCHEMA = {
     "type": "object",
     "required": ["version", "pipeline"],
     "properties": {
       "version": {"type": "string", "pattern": "^\\d+\\.\\d+$"},
       "pipeline": {
         "type": "object",
         "required": ["name", "stages"],
         "properties": {
           "name": {"type": "string"},
           "stages": {
             "type": "array",
             "items": {
               "type": "object",
               "required": ["name", "type"],
               "properties": {
                 "name": {"type": "string"},
                 "type": {"enum": [
                   "VERSION_SET", "PIPELINE_UPDATE", "PACKAGING",
                   "DEPLOYMENT", "CUSTOM", "APPROVAL"
                 ]}
               }
             }
           }
         }
       }
     }
   }

   def validate_structure(file_path):
     """Validate pipeline structure file"""
     try:
       with open(file_path, 'r') as f:
         data = json.load(f)

       jsonschema.validate(data, SCHEMA)
       print(f" Structure file is valid")
       return True, data

     except json.JSONDecodeError as e:
       print(f" JSON parsing error: {e}")
       return False, None

     except jsonschema.ValidationError as e:
       print(f" Schema validation error: {e.message}")
       return False, None

   def repair_structure(data):
     """Attempt to repair common issues"""
     repaired = False

     # Fix missing version
     if 'version' not in data:
       data['version'] = '1.0'
       repaired = True
       print("Fixed: Added missing version")

     # Fix missing pipeline name
     if 'pipeline' in data and 'name' not in data['pipeline']:
       data['pipeline']['name'] = 'unnamed-pipeline'
       repaired = True
       print("Fixed: Added missing pipeline name")

     # Ensure PipelineUpdate stage exists
     if 'pipeline' in data and 'stages' in data['pipeline']:
       has_update = any(s.get('type') == 'PIPELINE_UPDATE' 
                       for s in data['pipeline']['stages'])

       if not has_update:
         # Insert after VersionSet
         stages = data['pipeline']['stages']
         vs_index = next((i for i, s in enumerate(stages) 
                         if s.get('type') == 'VERSION_SET'), 0)

         stages.insert(vs_index + 1, {
           'name': 'PipelineUpdate',
           'type': 'PIPELINE_UPDATE'
         })
         repaired = True
         print("Fixed: Added missing PipelineUpdate stage")

     return repaired, data

   if __name__ == "__main__":
     file_path = sys.argv[1] if len(sys.argv) > 1 else "Pipeline.json"

     valid, data = validate_structure(file_path)

     if not valid and data:
       print("\nAttempting repairs...")
       repaired, data = repair_structure(data)

       if repaired:
         # Save repaired version
         backup_path = f"{file_path}.backup"
         import shutil
         shutil.copy(file_path, backup_path)

         with open(file_path, 'w') as f:
           json.dump(data, f, indent=2)

         print(f"\n Repaired structure saved")
         print(f"Original backed up to: {backup_path}")

